.nav {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  z-index: 9999;
  background-color: $white;

  &-top {
    @include betweenFlex;
    margin: 2rem $side-margin;
    flex-flow: wrap;
    margin-bottom: 2.5rem;

    &__logo {
      @include centerFlex;
      @include linkTrans;
    }

    &__search {
      font-size: 2rem;
      border: $black 1px solid;
      border-radius: 0;
      outline: none;
      min-width: 25%;
    }

    &__account {
      @include centerFlex;

      div {
        margin-right: 2.5rem;
      }
    }
  }

  &-bot {
    font-size: 1.5rem;
    border-top: #ccc 1px solid;
    border-bottom: #ccc 1px solid;

    &__container {
      @include betweenFlex;
      flex-flow: wrap;
      margin: .5rem $side-margin;

    }

    &__link {
      border-bottom: transparent 1px solid;
      transition: border .25s;

      &:active,
      &:hover {
        border-bottom: $near-black 1px solid;
      }
    }
  }

}

