//////////////////////////////
/// component  positioning ///
//////////////////////////////

@mixin centerFlex {
  display: flex;
  justify-content: center;
  align-items: center;
}

@mixin betweenFlex {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

@mixin fixedCenter {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

@mixin centerGrid {
  display: grid;
  justify-content: center;
  align-items: center;
}

@mixin verticalPageMargin {
  margin-top: 20rem;
  margin-bottom: 5rem;
}

//////////////////////////////
/// background positioning ///
//////////////////////////////

@mixin centerBG($url) {
  background-size: cover;
  background-repeat: none;
  background-position: center;
}

@mixin darkCenterBG($url) {
  $bg-opacity: 0.8;
  background: linear-gradient(0deg, rgba($black, $bg-opacity), rgba($black, $bg-opacity)), url($url);
  background-size: cover;
  background-repeat: none;
  background-position: center;
}

@mixin fixedBG {
  background-size: cover;
  background-repeat: no-repeat;
  background-attachment: fixed;
  background-position: center;
}

//////////////////////////////
////// general styling ///////
//////////////////////////////

@mixin boxShadow {
  box-shadow: 1rem 1rem 5rem rgba($black, 0.5);
}

@mixin boxShadow-thick {
  box-shadow: 1rem 1rem 5rem rgba($black, 0.75);
}

@mixin linkTrans {
  div {
    border-bottom: transparent 1px solid;
    transition: all .2s;

    &:hover,
    &:active {
      color: $near-black;
      border-bottom: $linkBB;
    }
  }
}

